import React, {Component} from 'react';

class Test extends Component{
  constructor(props){
    super(props)

    this.state = {
      title: '',
      amount: 0,
      products: [
        {
          title: 'Pepermint Oil',
          price: 10,
          image: 'https://images-na.ssl-images-amazon.com/images/I/71qYwmYFfVL._SL1334_.jpg'
        },
        {
          title: 'Lavender',
          price: 15,
          image: 'data:image/jpeg;base64,'          
        },
        {
          title: 'Lavender',
          price: 15,
          image: 'data:image/jpeg;base64,'          
        },
        {
          title: 'Lavender',
          price: 15,
          image: 'data:image/jpeg;base64,'          
        },
        {
          title: 'Lavender',
          price: 15,
          image: 'data:image/jpeg;base64,'          
        },
        {
          title: 'Lavender',
          price: 15,
          image: 'data:image/jpeg;base64,'          
        },
      ]
    }
    //bind functions here
    this.handleUpdateTitle = this.handleUpdateTitle.bind(this);
  }

  handleUpdateTitle(e){
    this.setState({
      title: e.target.value
    })
  }



  render() {

    let title;
    if(this.state.title.length > 0){
      title = this.state.title
    } else{
      title = 'Title Will Appear Here.'
    }

    let products = this.state.products.map((product, i) => {
      return(
        <div key={i}>
          <h1>{this.state.products[i].title}</h1>
          <img style={{"width":"200px"}} src={product.image} alt=""/>
        </div>
      )
    })

    return(
      <section>
        <input onChange={this.handleUpdateTitle} type="text"/>
        <h1>{title}</h1>
        {products}
      </section>
    )
  }

}

export default Test;